import React, { useEffect } from "react";
import styled from "styled-components";
import Product from "../Product/index.js";
import { useSelector, useDispatch } from "react-redux";
import { Button } from "@material-ui/core";


const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  box-shadow: 2px 2px 8px 0 rgb(255 90 90 /60%);
  background-color: rgba(0, 0, 0, 0.05);
`;

const OrderBox = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 15px;

  & > .MuiButton-containedSecondary {
    margin: 15px;
    background-color: #b73d1cbf;

    &:hover {
      background-color: #ff3600bf;
    }
  }
`;

const TotalPrize = styled.p`
  color: rgb(255 90 90 /90%);
  align-self: flex-end;
  font-size: 1.2em;
  margin: 20px;

  @media (min-width: 960px) {
    margin: 20px 40px;
  }
`;

const ProductsCart = () => {
  const products = [];
  return (
    <Wrapper>
      {products.map((product, index) => (
        <Product key={index} product={product} productIndex={index} />
      ))}
      <TotalPrize>Total prize: 12$</TotalPrize>

      <OrderBox>
        <Button type='submit' variant='contained' color='secondary'>
          Click to order
        </Button>
      </OrderBox>
    </Wrapper>
  );
};

export default ProductsCart;
