import { createSlice } from "@reduxjs/toolkit";

export const productsSlice = createSlice({
  name: "products",
  initialState: {
    products: [],
  },
  reducers: {
    increaseAmount: (state, action) => {
      if (
        state.products[action.payload].amount <=
        state.products[action.payload].availableAmount
      ) {
        state.products[action.payload].amount =
          state.products[action.payload].amount + 1;
      }
    },
    decreaseAmount: (state, action) => {
      if (state.products[action.payload].amount > 1) {
        state.products[action.payload].amount =
          state.products[action.payload].amount - 1;
      } else if ((state.products[action.payload].amount = 1)) {
        state.products.splice(action.payload, 1);
      }
    },
    addProductToCart: (state, action) => {
      if(state.products.includes(action.payload)){
        const existingProduct = state.products.find(product => product === action.payload)
        console.log(existingProduct)
      }
            state.products.push(action.payload);
    },
    clearCart: (state) => {
      state.products = [];
    },
  },
});

export const {
  increaseAmount,
  decreaseAmount,
  addProductToCart,
  clearCart,
} = productsSlice.actions;

export const selectProducts = (state) => state.products;

export default productsSlice.reducer;
