import React, { useState, useEffect } from "react";
import {
  Wrapper,
  Container,
  Nav,
  NavLogo,
  NavList,
  MobileNav,
  NavListItem,
  NavActions,
  Hamburger,
  HamburgerBox,
  HamburgerInner,
} from "./navigation.styles.js";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Link from "next/link";
import { useSelector } from "react-redux";
import { selectUser } from "../../Reducers/userSlice.js";

const Navigation = () => {
  const [user, setUser] = useState(null || useSelector(selectUser));
  const sliceUser = useSelector(selectUser);
  

  const handleClick = () => {
    const hamburger = document.querySelector(".hamburger__inner");
    const nav = document.querySelector(".nav");
    hamburger.classList.toggle("hamburger--active");
    nav.classList.toggle("active");
  };

  useEffect(() => {
    setUser(useSelector(selectUser));
  }, []);

  return (
    <>
      <MobileNav>
        <NavLogo>
          <Link href='/'>
            <a>
              <img src='/images/logo2.png' alt='logo' />
            </a>
          </Link>
        </NavLogo>
      </MobileNav>
      <Hamburger className='hamburger' onClick={() => handleClick()}>
        <HamburgerBox className='hamburger__box'>
          <HamburgerInner className='hamburger__inner'></HamburgerInner>
        </HamburgerBox>
      </Hamburger>
      <Wrapper className='nav'>
        <Container>
          <Nav>
            <NavLogo>
              <Link href='/'>
                <a>
                  <img src='/images/logo2.png' alt='logo' />
                </a>
              </Link>
            </NavLogo>

            <NavList>
              <NavListItem onClick={() => handleClick()}>
                <Link href='/'>
                  <a>Home</a>
                </Link>
              </NavListItem>

              <NavListItem onClick={() => handleClick()}>
                <Link href='#'>
                  <a>Games</a>
                </Link>
              </NavListItem>
              <NavListItem onClick={() => handleClick()}>
                <Link href='#'>
                  <a>Team</a>
                </Link>
              </NavListItem>
              <NavListItem onClick={() => handleClick()}>
                <Link href='#'>
                  <a>Contact</a>
                </Link>
              </NavListItem>
              {user ? (
                <NavListItem onClick={() => handleClick()}>
                  <Link href='/'>
                    <a>Log out</a>
                  </Link>
                </NavListItem>
              ) : (
                <NavListItem onClick={() => handleClick()}>
                  <Link href='/auth/register'>
                    <a>Join us</a>
                  </Link>
                </NavListItem>
              )}

              {user && (
                <NavListItem onClick={() => handleClick()}>
                  <Link href='/cart'>
                    <a>
                      <ShoppingCartIcon />({0})
                    </a>
                  </Link>
                </NavListItem>
              )}
            </NavList>
            <NavActions></NavActions>
          </Nav>
        </Container>
      </Wrapper>
    </>
  );
};

export default Navigation;
