import React from "react";
import styled from "styled-components";
import Typing from "react-typing-animation";

const Wrapper = styled.div`
  display: flex;
  width: 100%;
  min-height: 100vh;
  background: url("/images/bgdekstop.jpg") no-repeat center;
  background-size: cover;
  align-items: center;
  font-family: "Roboto";
  justify-content: center;
`;

const HeaderContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 90%;
  margin: 0 auto;

  @media (min-width: 960px) {
    width: 100%;
  }
`;

const Header = styled.h1`
  display: flex;
  justify-content: center;
  align-items: center;
  font-weight: 900;
  text-align: center;

  @media (min-width: 960px) {
    font-size: 50px;
  }
`;

const Hero = () => {
  return (
    <Wrapper>
      <Typing hideCursor={true}>
        <HeaderContainer>
          <Typing.Delay ms={500} />
          <Header>All of us love gaming</Header>
          <Typing.Delay ms={1500} />
          click to buy something to love.
        </HeaderContainer>
      </Typing>
    </Wrapper>
  );
};

export default Hero;
