import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Email from "../Email/index.js";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  align-items: center;
`;

const Heading = styled.h1`
  width: 100%;
  text-align: center;
  padding: 40px;
  color: rgb(255 90 90 /90%);
`;

const EmailsCart = () => {
  const [emails, setEmails] = useState([]);

  const fetchEmails = async () => {
    try {
      const query = await fetch("http://localhost:8080/getEmails");
      const response = await query.json();
      setEmails(response.emails);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    let isMounted = true;

    fetchEmails();
    return () => {
      isMounted = false;
    };
  }, []);

  return (
    <Wrapper>
      {emails && emails.length === 0 ? (
        <Heading>We don't have any emails</Heading>
      ) : (
        ((<Heading>Emails: </Heading>),
        emails.map((email, index) => <Email email={email} index={index} />))
      )}
    </Wrapper>
  );
};

export default EmailsCart;
