import React from "react";
import styled from "styled-components";
import { useForm } from "react-hook-form";
import Input from "@material-ui/core/Input";
import { FormLabel, Button } from "@material-ui/core";

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: space-between;
  min-width: 35vw;
  max-width: 500px;
  min-height: 40vh;
  padding: 10px;
  color: white;
  box-shadow: 2px 2px 8px 0 rgb(0 0 0 / 60%);
  background-color: rgba(0, 0, 0, 0.3);
`;

const Form = styled.form`
  display: flex;
  width: 100%;
  height: 100%;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  color: white;

  & > .MuiInputBase-input {
    color: white;
  }
`;

const ErrorSpan = styled.span`
  color: red;
  font-size: 12px;
`;

const Header = styled.h1`
  display: flex;
  font-weight: 700;
`;

const InputElement = styled(Input)`
  margin: 5px 0;
`;

const defaultValues = {
  firstName: "",
  lastName: "",
  email: "",
  password: "",
};

const RegisterForm = () => {
  const { register, handleSubmit, errors, setError, reset } = useForm({
    defaultValues,
  });

  return (
    <Wrapper>
      <Header>Create an account</Header>

      <Form>
        <FormLabel>
          <InputElement
            type='text'
            name='firstName'
            placeholder='First Name'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("firstName", {
                type: "manual",
                message: "You have to provide a first name",
              });
            }}
          />
          {errors.firstName && errors.firstName.type === "required" && (
            <ErrorSpan>Please provide a first name</ErrorSpan>
          )}
        </FormLabel>
        <FormLabel>
          <InputElement
            type='text'
            name='lastName'
            placeholder='Last Name'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("lastName", {
                type: "manual",
                message: "You have to provide a last name",
              });
            }}
          />
          {errors.lastName && errors.lastName.type === "required" && (
            <ErrorSpan>Please provide a last name</ErrorSpan>
          )}
        </FormLabel>
        <FormLabel>
          <InputElement
            type='text'
            name='email'
            placeholder='Enter email'
            inputRef={register({
              required: true,
              pattern: {
                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                message: "invalid email address",
              },
            })}
            onChange={() => {
              setError("email", {
                type: "manual",
                message: "You have to a email",
              });
            }}
          />
          {errors.email && errors.email.type === "required" && (
            <ErrorSpan>Please provide a email</ErrorSpan>
          )}
        </FormLabel>
        <FormLabel>
          <InputElement
            type='password'
            name='password'
            placeholder='Enter Password'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("password", {
                type: "manual",
                message: "You have to provide a password",
              });
            }}
          />
          {errors.password && errors.password.type === "required" && (
            <ErrorSpan>Please provide a password</ErrorSpan>
          )}
        </FormLabel>
      </Form>
    </Wrapper>
  );
};

export default RegisterForm;
