import React from "react";
import styled from "styled-components";
import { useForm } from "react-hook-form";
import Input from "@material-ui/core/Input";
import { FormLabel, Button, Checkbox } from "@material-ui/core";
import Link from "next/link";
import Alert from "../Alert/index";
import IconButton from "@material-ui/core/IconButton";
import PhotoCamera from "@material-ui/icons/PhotoCamera";

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: space-between;
  min-width: 35vw;
  max-width: 500px;
  min-height: 40vh;
  padding: 10px;
  color: white;
  box-shadow: 2px 2px 8px 0 rgb(0 0 0 / 60%);
  background-color: rgba(0, 0, 0, 0.3);
`;

const Form = styled.form`
  display: flex;
  width: 100%;
  height: 100%;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  color: white;

  & > .MuiButton-containedSecondary {
    margin: 15px;
    background-color: #b73d1cbf;

    &:hover {
      background-color: #ff3600bf;
    }
  }

  & > .MuiFormLabel-root {
    color: white;
  }

  & > .hidden {
    display: none;
  }

  & > label > .MuiIconButton-colorPrimary {
    color: #ff3600bf !important;
  }
`;

const ErrorSpan = styled.span`
  color: red;
  font-size: 12px;
`;

const Header = styled.h2`
  display: flex;
  font-weight: 700;
`;

const InputElement = styled(Input)`
  margin: 8px 0;
  color: white;

  & > .MuiInputBase-input {
    color: white;
  }
`;

const CheckBox = styled(FormLabel)`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  margin: 15px 0;

  & > .MuiCheckbox-root {
    color: #b73d1cbf;
  }

  & > .MuiCheckbox-colorSecondary.Mui-checked {
    color: #b73d1cbf;
  }
`;

const defaultValues = {
  firstName: null,
  lastName: null,
  email: null,
  password: null,
  policy: false,
  avatar: null,
};

const RegisterForm = () => {
  const { register, handleSubmit, errors, setError, reset } = useForm({
    defaultValues,
  });

  const registerUser = (data, event) => {
    event.preventDefault();
    console.log(data);
  };

  return (
    <Wrapper>
      <Header>Create an account</Header>

      <Form onSubmit={handleSubmit(registerUser)}>
        <FormLabel>
          <InputElement
            type='text'
            name='firstName'
            placeholder='First Name'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("firstName", {
                type: "manual",
                message: "You have to provide a first name",
              });
            }}
          />
        </FormLabel>
        {errors.firstName && errors.firstName.type === "required" && (
          <ErrorSpan>Please provide a first name</ErrorSpan>
        )}
        <FormLabel>
          <InputElement
            type='text'
            name='lastName'
            placeholder='Last Name'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("lastName", {
                type: "manual",
                message: "You have to provide a last name",
              });
            }}
          />
        </FormLabel>
        {errors.lastName && errors.lastName.type === "required" && (
          <ErrorSpan>Please provide a last name</ErrorSpan>
        )}
        <FormLabel>
          <InputElement
            type='text'
            name='email'
            placeholder='Enter email'
            inputRef={register({
              required: true,
              pattern: {
                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                message: "invalid email address",
              },
            })}
            onChange={() => {
              setError("email", {
                type: "manual",
                message: "You have to a email",
              });
            }}
          />
        </FormLabel>
        {errors.email && errors.email.type === "required" && (
          <ErrorSpan>Please provide a email</ErrorSpan>
        )}
        {errors.email && errors.email.type === "pattern" && (
          <ErrorSpan>Please provide a correct email</ErrorSpan>
        )}
        <FormLabel>
          <InputElement
            type='password'
            name='password'
            placeholder='Enter Password'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("password", {
                type: "manual",
                message: "You have to provide a password",
              });
            }}
          />
        </FormLabel>
        {errors.password && errors.password.type === "required" && (
          <ErrorSpan>Please provide a password</ErrorSpan>
        )}
        <input
          type='file'
          name='avatar'
          id='avatar'
          accept='image/*'
          className='hidden'
          ref={register({ required: true })}
          onChange={(e) => {
            setError("picture", {
              type: "manual",
              message: "You have to provide an avatar",
            });
            setFile(e.target.files[0]);
          }}
        />
        <label htmlFor='avatar'>
          <IconButton
            color='primary'
            aria-label='upload picture'
            component='span'
          >
            <PhotoCamera />
          </IconButton>
        </label>
        {errors.avatar && errors.avatar.type === "required" && (
          <ErrorSpan>Please provide an avatar</ErrorSpan>
        )}
        <CheckBox>
          {" "}
          You have to accept our{" "}
          <Link href='/privacy'>
            <a>Privacy Policy</a>
          </Link>
          <Checkbox
            name='policy'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("policy", {
                type: "manual",
                message: "You have accept our privacy policy ",
              });
            }}
          />
          {errors.policy && errors.policy.type === "required" && (
            <ErrorSpan>Please accept our privacy policy</ErrorSpan>
          )}
        </CheckBox>

        <Button type='submit' variant='contained' color='secondary'>
          Create
        </Button>
      </Form>
    </Wrapper>
  );
};

export default RegisterForm;
