import React, { useEffect } from "react";
import styled from "styled-components";
import Product from "../Product/index.js";
import { Button } from "@material-ui/core";


const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  box-shadow: 2px 2px 8px 0 rgb(255 90 90 /60%);
  background-color: rgba(0, 0, 0, 0.05);
`;

const OrderBox = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 15px;

  & > .MuiButton-containedSecondary {
    margin: 15px;
    background-color: #b73d1cbf;

    &:hover {
      background-color: #ff3600bf;
    }
  }
`;

const TotalPrize = styled.p`
  color: #5bb2fc;
`;

const ProductsCart = () => {
  const products = [
    {
      productImg:
        "https://images.unsplash.com/photo-1589241062272-c0a000072dfa?ixlib=rb-1.2.1&auto=format&fit=crop&w=967&q=80",
      productName: "Fortnite ",
      price: 20,
      productDescription: "Very nice game :)",
      gameType: "XD",
      amount: 1,
    },
  ];
  return (
    <Wrapper>
      {products.map((product, index) => (
        <Product key={index} product={product} productIndex={index} />
      ))}
      <OrderBox>
        <Button type='submit' variant='contained' color='secondary'>
          Click to order
        </Button>
        <TotalPrize>12 %</TotalPrize>
      </OrderBox>
    </Wrapper>
  );
};

export default ProductsCart;
