import React, { useState, useEffect } from "react";
import {
  Wrapper,
  Container,
  Nav,
  NavLogo,
  NavList,
  MobileNav,
  NavListItem,
  NavActions,
  Hamburger,
  HamburgerBox,
  HamburgerInner,
} from "./navigation.styles.js";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Link from "next/link";
import { useSelector, useDispatch } from "react-redux";
import { selectUser, deleteUser } from "../../Reducers/userSlice.js";
import { selectProducts } from "../../Reducers/productsSlice.js";

const Navigation = () => {
  const [user, setUser] = useState({ user: null });
  const sliceUser = useSelector(selectUser);
  const productsArray = useSelector(selectProducts);
  const dispatch = useDispatch();

  const handleClick = () => {
    const hamburger = document.querySelector(".hamburger__inner");
    const nav = document.querySelector(".nav");
    hamburger.classList.toggle("hamburger--active");
    nav.classList.toggle("active");
  };

  const logOut = () => {
    dispatch(deleteUser());
    setUser({ user: null });
  };

  useEffect(() => {
    setUser(sliceUser);
  }, [sliceUser]);

  return (
    <>
      <MobileNav>
        <NavLogo>
          <Link href='/'>
            <a>
              <img src='/images/logo2.png' alt='logo' />
            </a>
          </Link>
        </NavLogo>
      </MobileNav>
      <Hamburger className='hamburger' onClick={() => handleClick()}>
        <HamburgerBox className='hamburger__box'>
          <HamburgerInner className='hamburger__inner'></HamburgerInner>
        </HamburgerBox>
      </Hamburger>
      <Wrapper className='nav'>
        <Container>
          <Nav>
            <NavLogo>
              <Link href='/'>
                <a>
                  <img src='/images/logo2.png' alt='logo' />
                </a>
              </Link>
            </NavLogo>

            <NavList>
              <NavListItem onClick={() => handleClick()}>
                <Link href='/'>
                  <a>Home</a>
                </Link>
              </NavListItem>

              <NavListItem onClick={() => handleClick()}>
                <Link href='#'>
                  <a>Games</a>
                </Link>
              </NavListItem>
              <NavListItem onClick={() => handleClick()}>
                <Link href='#'>
                  <a>Team</a>
                </Link>
              </NavListItem>
              <NavListItem onClick={() => handleClick()}>
                <Link href='#'>
                  <a>Contact</a>
                </Link>
              </NavListItem>
              {user.user !== null ? (
                <NavListItem
                  onClick={() => {
                    handleClick(), logOut();
                  }}
                >
                  <Link href='/'>
                    <a>Log out</a>
                  </Link>
                </NavListItem>
              ) : (
                <NavListItem onClick={() => handleClick()}>
                  <Link href='/auth/register'>
                    <a>Join us</a>
                  </Link>
                </NavListItem>
              )}

              {user.user !== null && (
                <NavListItem onClick={() => handleClick()}>
                  <Link href='/auth/account/cart'>
                    <a>
                      <ShoppingCartIcon />({0})
                    </a>
                  </Link>
                </NavListItem>
              )}
            </NavList>
            <NavActions></NavActions>
          </Nav>
        </Container>
      </Wrapper>
    </>
  );
};

export default Navigation;
