import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  font-family: "Roboto";
  width: 100%;
  padding: 15px;
`;

const SubHeader = styled.h3`
  display: flex;
  font-weight: 900;
  font-size: 1.2em;
  text-align: center;
  @media (min-width: 960px) {
    font-size: 2.5em;
  }
`;

const Header = styled.h2`
  color: red;
  font-size: 1.7em;
  text-align: center;
  font-family: "Black Ops One", cursive;
  letter-spacing: 1px;
  text-transform: uppercase;

  @media (min-width: 960px) {
    font-size: 3em;
  }
`;

const Description = styled.p`
  margin: 25px 15px;
  display: block;
  text-align: center;
  font-size: 0.9em;
  color: rgba(0, 0, 0, 0.8);

  @media (min-width: 960px) {
    font-size: 1.1em;
    width: 60%;
    margin: auto;
  }
`;

const GameTypes = () => {
  return (
    <Wrapper>
      <SubHeader>Welcome in our</SubHeader>
      <Header>online gaming</Header>
      <SubHeader>Shop</SubHeader>
      <Description>
        We are one of the largest sellers of computer games on the web, and our
        company is characterized by a fast supplier and high quality of services
      </Description>
    </Wrapper>
  );
};

export default GameTypes;
