import React, { useState } from "react";
import styled from "styled-components";
import { useForm } from "react-hook-form";
import Input from "@material-ui/core/Input";
import { FormLabel, Button, Checkbox } from "@material-ui/core";
import IconButton from "@material-ui/core/IconButton";
import PhotoCamera from "@material-ui/icons/PhotoCamera";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";

const Form = styled.form`
  display: flex;
  width: 100%;
  height: 100%;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  color: white;

  & > .MuiButton-containedSecondary {
    margin: 15px;
    background-color: #b73d1cbf;

    &:hover {
      background-color: #ff3600bf;
    }
  }

  & > .MuiFormLabel-root {
    color: white;
  }

  & > .hidden {
    display: none;
  }

  & > label > .MuiIconButton-colorPrimary {
    color: #ff3600bf !important;
  }
`;

const ErrorSpan = styled.span`
  color: #ff5a5a;
  font-size: 12px;

  & > a {
    cursor: pointer;
  }
`;

const Header = styled.h2`
  display: flex;
  font-weight: 700;
  width: 100%;
  padding: 10px;
  justify-content: center;
  color: rgb(255 90 90 /60%);
`;

const InputElement = styled(Input)`
  margin: 8px 0;
  color: white;

  & > .MuiInputBase-input {
    color: white;
  }
`;

const defaultValues = {
  firstName: null,
  lastName: null,
  email: null,
  password: null,
  avatar: null,
  policy: null,
};

const AddProductForm = () => {
  const { register, handleSubmit, errors, setError, reset } = useForm({
    defaultValues,
  });

  const [responseType, setResponseType] = useState(null);
  const clearAlert = () => setTimeout(() => setResponseType(null), 999);

  const registerUser = async (data, event) => {
    event.preventDefault();
    const { firstName, lastName, email, password, avatar, policy } = data;

    const user = new FormData();
    user.append("firstName", firstName);
    user.append("lastName", lastName);
    user.append("email", email);
    user.append("password", password);
    user.append("avatar", avatar[0]);
    user.append("policy", policy);

    try {
      const send = await fetch("http://localhost:8080/addProduct", {
        method: "POST",
        body: user,
      });
      const response = await send.json();
      if (response) {
        setResponseType(response);
        if (response.product) {
          reset();
        }
      }
    } catch (err) {
      setResponseType({ message: "Some error occured, try again" });
    }
    clearAlert();
  };

  return (
    <div>
      <Header>Add product</Header>
      <Form onSubmit={handleSubmit(registerUser)} encType=''>
        <FormLabel>
          <InputElement
            type='text'
            name='productName'
            placeholder='Product Name'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("productName", {
                type: "manual",
                message: "You have to provide a product name",
              });
            }}
          />
        </FormLabel>
        {errors.productName && errors.productName.type === "required" && (
          <ErrorSpan>Please provide a product name</ErrorSpan>
        )}

        <FormLabel>
          <InputElement
            type='text'
            name='productDescription'
            placeholder='Product Description'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("productDescription", {
                type: "manual",
                message: "You have to provide a product description",
              });
            }}
          />
        </FormLabel>
        {errors.productDescription &&
          errors.productDescription.type === "required" && (
            <ErrorSpan>Please provide a product description</ErrorSpan>
          )}

        <FormLabel>
          <Select></Select>
        </FormLabel>
        {errors.email && errors.email.type === "required" && (
          <ErrorSpan>Please provide a email</ErrorSpan>
        )}
        {errors.email && errors.email.type === "pattern" && (
          <ErrorSpan>Please provide a correct email</ErrorSpan>
        )}

        <FormLabel>
          <InputElement
            type='number'
            name='prize'
            placeholder='Enter prize'
            inputRef={register({ required: true })}
            onChange={() => {
              setError("prize", {
                type: "manual",
                message: "You have to provide a prize",
              });
            }}
          />
        </FormLabel>
        {errors.prize && errors.prize.type === "required" && (
          <ErrorSpan>Please provide a prize</ErrorSpan>
        )}

        <FormLabel>
          <InputElement
            type='number'
            name='amount'
            placeholder='Enter amount of products '
            inputRef={register({ required: true, minValue: 5 })}
            onChange={() => {
              setError("amount", {
                type: "manual",
                message: "You have to provide amount of products",
              });
            }}
          />
        </FormLabel>
        {errors.amount && errors.amount.type === "required" && (
          <ErrorSpan>Please provide a amount of products</ErrorSpan>
        )}

        <input
          ref={register({ required: true })}
          name='productImg'
          type='file'
          accept='.png, .jpg, .jpeg'
          id='productImg'
          className='hidden'
          onChange={(e) => {
            setError("productImg", {
              type: "manual",
              message: "You have to provide an product image",
            });
          }}
        />
        <label htmlFor='productImg'>
          <IconButton
            color='primary'
            aria-label='upload picture'
            component='span'
          >
            <PhotoCamera />
          </IconButton>
        </label>
        {errors.productImg && errors.productImg.type === "required" && (
          <ErrorSpan>Please provide a product image</ErrorSpan>
        )}

        <Button type='submit' variant='contained' color='secondary'>
          Create
        </Button>
      </Form>
    </div>
  );
};

export default AddProductForm;
