import React from "react";
import styled from "styled-components";
import IconButton from "@material-ui/core/IconButton";
import { Button } from "@material-ui/core";

const Card = styled.div`
  display: flex;
  flex-direction: column;

  & > .MuiButton-containedSecondary {
    margin: 15px;
    background-color: #b73d1cbf;
    width: fit-content;
    align-self: center;

    &:hover {
      background-color: #ff3600bf;
    }
  }

  @media (min-width: 960px) {
    max-width: 300px;
  }
`;

const CardImage = styled.div`
  display: flex;

  img {
    width: 100%;
    height: auto;
  }
`;

const CardContentContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const CardContentHeader = styled.h4`
  width: 100%;
`;

const OfferProduct = ({ product }) => {
  const {
    productName,
    productDescription,
    gameType,
    prize,
    amount,
    productImg,
  } = product;
  return (
    <Card>
      <CardImage>
        <img src={`http://localhost:8080/${productImg}`} alt={productName} />
      </CardImage>
      <CardContentContainer>
        <CardContentHeader>{productName}</CardContentHeader>
      </CardContentContainer>
      <Button variant='contained' color='secondary'>
        Add
      </Button>
    </Card>
  );
};

export default OfferProduct;
