import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;

  @media (min-width: 960px) {
    width: 40%;
  }
`;

const UserBox = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  box-shadow: 2px 2px 8px 0 rgb(0 0 0 / 60%);
  background-color: rgba(0, 0, 0, 0.3);
`;

const UserAvatar = styled.div`
  width: 100px;
  height: 100px;
  border-radius: 50%;
  img {
    width: 100%;
    height: auto;
  }

  @media (min-width: 960px) {
    width: 200px;
    height: 200px;
  }
`;

const UserCart = ({ user }) => {
  const { firstName, lastName, avatar, email } = user.user;
  console.log(user);
  return (
    <Wrapper>
      <UserBox>
        <UserAvatar>
          <img src={`http://locahost:3000/${avatar}`} alt={firstName} />
        </UserAvatar>
      </UserBox>
    </Wrapper>
  );
};

export default UserCart;
