import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 100%;
`;

const ProductContainer = styled.div`
  display: flex;
  flex-direction: column;

  @media (min-width: 960px) {
    flex-direction: row;
  }
`;

const ProductImage = styled.div`
  display: flex;
  height: 60px;
  width: 6px;

  @media (min-width: 960px) {
    height: 120px;
    width: 120px;
  }

  img {
    width: 100%;
    height: auto;
  }
`;

const Product = ({ product, productIndex }) => {
  const {
    productImg,
    productName,
    price,
    productDescription,
    gameType,
    amount,
  } = product;
  return (
    <Wrapper>
      <ProductContainer>
        <ProductImage>
          <img src={productImg} alt={productName} />
        </ProductImage>
      </ProductContainer>
    </Wrapper>
  );
};

export default Product;
