import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  background-color: #24272e;
  justify-content: center;
  box-shadow: 2px 2px 8px 0 rgb(0 0 0 / 60%);
`;

const FooterContainer = styled.footer`
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: column;
  width: 100%;
  padding: 15px;

  @media (min-width: 960px) {
    width: 90%;
    flex-direction: row;
  }

  @media (min-width: 1280px) {
    width: 80%;
  }
`;

const Heading = styled.h3`
  display: block;
  letter-spacing: 2px;
  color: #5bb2fc;
  font-family: "Black Ops One", normal;
  font-weight: 500;
`;

const AdressContainer = styled.div`
  display: flex;
  flex-direction: column;
  color: rgba(255, 255, 255, 0.8);
  height: 100%;
`;

const AdressContent = styled.div`
  text-align: left;
  margin: 10px 0;

  span {
    margin: 10px 0;
    color: #ff5a5a;
  }
`;

const FollowUsContainer = styled.div`
  display: flex;
  height: 100%;
  flex-direction: column;
`;

const LinksList = styled.ul`
  display: flex;
  width: 100%;
  align-items: center;
  justify-content: space-between;

  li > a {
    display: block;
    width: 50px;
    height: 50px;
    text-decoration: none;
    transition: 0.5s all;
    background: url("/images/img-sprite.png") no-repeat 0px 0px;

    & > .facebook {
      background-position: 0px 0px;
    }

    & > .twitter {
      background-position: -50px 0px;
    }

    & > .google {
      background-position: -100px 0px;
    }

    & > .instagram {
      background-position: -150px 0px;
    }

    & > .youtube {
      background-position: -200px 0px;
    }
  }
`;

const Footer = () => {
  return (
    <Wrapper>
      <FooterContainer>
        <AdressContainer>
          <Heading>Adress</Heading>
          <AdressContent>
            <address>
              Lorem Ipsum
              <br />
              HTML5 Buldings
              <br />
              Doctorville
              <br />
              Great Britain
              <br />
              (123) 456- 7890
              <br />
              <br />
              <span>Phone: +123 4567 8900</span>
            </address>
          </AdressContent>
        </AdressContainer>
        <FollowUsContainer>
          <Heading>Follow Us</Heading>
          <LinksList>
            <li>
              <a className='facebook' href='#'></a>
            </li>
            <li>
              <a className='twitter' href='#'></a>
            </li>
            <li>
              <a className='google' href='#'></a>
            </li>
            <li>
              <a className='instagram' href='#'></a>
            </li>
            <li>
              <a className='youtube' href='#'></a>
            </li>
          </LinksList>
        </FollowUsContainer>
      </FooterContainer>
    </Wrapper>
  );
};

export default Footer;
