import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  font-family: "Roboto";
  width: 100%;
  padding: 15px;

  @media (min-width: 960px) {
    padding: 35px;
  }
`;

const SubHeader = styled.h3`
  display: flex;
  font-weight: 900;
  font-size: 1.2em;
  text-align: center;
  @media (min-width: 960px) {
    font-size: 2.5em;
  }
`;

const Header = styled.h2`
  color: red;
  font-size: 1.7em;
  text-align: center;
  font-family: "Black Ops One", cursive;
  letter-spacing: 1px;
  text-transform: uppercase;

  @media (min-width: 960px) {
    font-size: 3em;
  }
`;

const Description = styled.p`
  margin: 25px 15px;
  display: block;
  text-align: center;
  font-size: 0.9em;
  color: rgba(0, 0, 0, 0.8);

  @media (min-width: 960px) {
    padding: 50px 15px;
    font-size: 1.1em;
    width: 70%;
    margin: auto;
  }
`;

const CardsWrapper = styled.div`
  display: grid;
  grid-template-columns: 1;
  grid-gap: 20px;
  grid-template-rows: repeat(3, 1fr);

  @media (min-width: 960px) {
    width: 90%;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: 1;
  }

  @media (min-width: 1280px) {
    width: 85%;
  }
`;

const Card = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
`;

const CardImage = styled.div`
  display: flex;
  width: 100%;

  img {
    max-width: 100%;
  }
`;

const CardTitle = styled.h4`
  display: block;
  width: 100%;
  text-align: center;
  font-weight: 900;
  padding: 5px;
`;

const CardContent = styled.p`
  display: flex;
  width: 100%;
  text-align: center;
  padding: 5px;
  color: rgba(0, 0, 0, 0.8);
`;

const GameTypes = () => {
  return (
    <Wrapper>
      <SubHeader>Welcome in our</SubHeader>
      <Header>online gaming</Header>
      <SubHeader>Shop</SubHeader>
      <Description>
        We are one of the largest sellers of computer games on the web, and our
        company is characterized by a fast supplier and high quality of
        services. Some of the games categories:
      </Description>
      <CardsWrapper>
        <Card>
          <CardImage>
            <img src='/images/ai1.jpg' alt='Battle' />
          </CardImage>
        </Card>
        <Card>
          <CardImage>
            <img src='/images/ai2.jpg' alt='Racing' />
          </CardImage>
        </Card>
        <Card>
          <CardImage>
            <img src='/images/ai3.jpg' alt='battle' />
          </CardImage>
        </Card>
      </CardsWrapper>
    </Wrapper>
  );
};

export default GameTypes;
